import React,  {useState, useEffect} from 'react';
import Axios from 'axios';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form'
import Card from 'react-bootstrap/Card'
import 'bootstrap/dist/css/bootstrap.min.css';

/* <button onClick={submitProduct} style={{color:'red'}}>Submit Me </button> -->

 <button onClick={getInfoViri}> Say Hola</button>
// */



function Display(props){
  const [pName, setPName] = useState('NoproductName');
  const [quantity, setQuantity] = useState(0);


  const submitProduct = () => {
    Axios.post('http://localhost:5000/api/lookTheItem',
     {nameP:pName,
      quantityP: quantity,
      barcodeP:props.mydata
     }).then(()=> {
       alert('Sucessful insert');
     });
  };



  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);


  useEffect(() => {
  Axios("http://localhost:5000/api/holav")
  .then((response) => {
  setQuantity(response.data);
  })
  .catch((error) => {
  console.error("Error fetching data: ", error);
  setError(error);
  })
  .finally(() => {
  setLoading(false);
  });
  }, []);


  const getInfoViri = () => {
    Axios.get('http://localhost:5000/holav2', {
   params:  {
      barcodeP: props.mydata


    }
  })
    .then((response) => {
      setQuantity(response.data)
    })
  }



  return (
    <>


   <div>

<Form>
 <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
     <p>{props.mydata}</p>
  <Card.Title>Name of the Product</Card.Title>

  <input type = "text" name="productName" onChange={(e) => {
    setPName(e.target.value)
  }}/>
  </Form.Group>

  <Card.Title>Quantity</Card.Title>

  <input type = "text" name="quantity" onChange={(e) => {
    setQuantity(e.target.value)
  }}/>

</Form>



<>
<div>
  <Button variant="outline-primary" onClick={submitProduct}>Update Quantity</Button>
</div>
</>

<>
<div>
   <Button variant="outline-dark" onClick={getInfoViri}>Check Quantity</Button>
</div>
</>
<Card.Title>Current Quantity</Card.Title>

  <p>{quantity} </p>
  </div>




  </>
  )
}

export default Display;
