import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';
import BarcodeScannerComponent from "react-webcam-barcode-scanner";
import Display from './Components/Display.js'







function App() {





   const [ BarData, setBarData ] = React.useState('');

   const [quantityData, setQuantityData]  = React.useState('')

   // useEffect(() => {
   //   fetch("https://localhost:5000/hola")
   //   .then(response => response.json())
   //   .then(data => setQuantityData(data.message.text))
   //   .catch(error => alert('Error!' + error.message))
   // },[])


   useEffect(() => {
       const url = "https://localhost:5000/holav";

       const fetchData = async () => {
         try {
           const response = await fetch(url);
           const json = await response.json();
           console.log(json);
         } catch (error) {
           console.log("error", error);
         }
       };

       fetchData();
   }, []);





   return (
     <>
       <BarcodeScannerComponent
         width={500}
         height={500}
         onUpdate={(err, result) => {
           //console.log(result)
           if (result){
             setBarData(result.text)
           }
           //else setData('Not found item')
           //onsole.log(err)
         }}
       />

      <Display
      mydata={BarData}/>

      <div> {quantityData && quantityData.map((item) => <p> {item} </p>) }</div>

     </>
   )
}



export default App;
