import logo from './logo.svg';
import './App.css';
import React, { useEffect } from 'react';
import BarcodeScannerComponent from "react-webcam-barcode-scanner";
import Display from './Components/Display.js'

function App() {

   const [ BarData, setBarData ] = React.useState('');

   const [quantityData, setQuantityData]  = React.useState('')

   useEffect(() => {
     fetch("localhost:8000/api/insert")
     .then(response => response.json())
     .then(data => setQuantityData(data.message))
   },[])

   return (
     <>
       <BarcodeScannerComponent
         width={500}
         height={500}
         onUpdate={(err, result) => {
           console.log(result)
           if (result){
             setBarData(result.text)
           }
           //else setData('Not found item')
           //onsole.log(err)
         }}
       />

      <Display
      mydata={BarData}/>

      <div> {
        quantityData && quantityData.map((item) => <p> {item} </p>)
      }</div>

     </>
   )
}



export default App;
